// Verify the library is loaded
if (typeof CryptoJS === 'undefined') {
    console.error("Error: CryptoJS is not loaded! Check your HTML.");
} else {
    console.log("Success: CryptoJS is loaded!");

    // Try a simple hash
    var hash = CryptoJS.SHA256("Hello World");
    console.log("SHA256 Hash:", hash.toString());
}